import Link from "next/link";
import Head from "next/head";
import Master from "../../../app_components/commonHeader";
import { get } from "http";
import {useRouter} from 'next/router';

function PostData({data}){  //data={type:Buffer,data:[]
   var post_ = (Buffer.from(data)).toString('utf8');
    return <div dangerouslySetInnerHTML={{__html:post_}}></div>;
}


export default function Post({ latest, post,title,thum}) {

  console.log(latest, post);

  return (
    <Master thum={thum} description={title}>
    <Head>
       
    </Head>
      <body>
        <nav class="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
          <div class="container">
            <a class="navbar-brand" href="#">
              Tech-Inventory
            </a>
            <button
              class="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target="#navbarResponsive"
              aria-controls="navbarResponsive"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarResponsive">
              <ul class="navbar-nav ml-auto">
                <li class="nav-item active">
                  <a class="nav-link" href="#">
                    Home
                    <span class="sr-only">(current)</span>
                  </a>
                </li>
                <li class="nav-item">
                  <Link href="/about">
                    <a class="nav-link" href="#">
                      About
                    </a>
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>
    
        <div class="container">

        <div class="row">
    
          <div class="col-lg-8">
    
        
            <h1 class="mt-4">{post.title}</h1>

            <hr/>
              <p>Posted on {post.date}</p>
            <hr/>

            <img class="img-fluid rounded" src={thum||"http://placehold.it/750x300"} alt="" />
            <hr />
           <PostData data={post.fileData}/>
          </div>
    
          
          <div class="col-md-4">

            <div class="card my-4">
              <h5 class="card-header">Search</h5>
              
              <div class="card-body">
                <div class="input-group">
                  <input type="text" class="form-control" placeholder="Search for..." />
                  <span class="input-group-append">
                    <button class="btn btn-secondary" type="button">Go!</button>
                  </span>
              </div>
            </div>
      </div>
    
        
      <div class="card my-4">
      <h5 class="card-header">Latest</h5>
      <div class="card-body">
        <div class="row">
          <div class="col-lg-6">
            <ul class="list-unstyled mb-0">
              {latest &&
                latest.map((post, index) => {
                  return (
                    <li key={index}>
                      <a href="#">{post.title}</a>
                    </li>
                  );
                })}
            </ul>
          </div>
        </div>
      </div>
    </div>

    
      
            <div class="card my-4">
              <h5 class="card-header">Side Widget</h5>
              <div class="card-body">
                You can put anything you want inside of these side widgets. They are easy to use, and feature the new Bootstrap 4 card containers!
              </div>
            </div>
          </div>  
        </div>
      </div>

        <footer class="py-5 bg-dark">
          <div class="container">
            <p class="m-0 text-center text-white">
              Copyright &copy; Your Website 2020
            </p>
          </div>
        </footer>
      </body>
    </Master>
  );
}

function fetcher(url) {
  var data = [];
  const initFetch = new Promise((resolve, reject) => {
    get(url, function (res_) {
      res_.on("data", (chunk) => {
        data.push(chunk);
      });

      res_.on("close", (_) => {
        console.log("closing");
        var data_Bytes = Buffer.concat(data);
        var data_json = JSON.parse(data_Bytes.toString());
        resolve(data_json);
      });
    });
  });

  return initFetch;
}

export async function getServerSideProps(context) {
  
  console.log(context);
  //  const router = useRouter();
    const {id,thum,title} = router.query; 
  const post = await fetcher("http://localhost:4000/post/"+`${id}`);
  const latest = await fetcher("http://localhost:4000/latestPosts");

  return {
    props: {
      latest,
      post,
      thum,
      title
    },
  };
}
